import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useMatch,
} from "react-router-dom";

import { setNotification } from "./reducers/notificationReducer";
import { initializeBlogs } from "./reducers/blogsReducer";
import { loginUser, logoutUser, loadUser } from "./reducers/userReducer";
import { loadUsers } from "./reducers/usersReducer";

import LoginForm from "./components/Login";
import Notification from "./components/Notification";
import Blogs from "./components/Blogs";
import Users from "./components/Users";
import User from "./components/User";

const App = () => {
  const dispatch = useDispatch();

  const user = useSelector((state) => {
    // console.log("User: ", state.user);
    return state.user;
  });

  const users = useSelector((state) => state.users);

  const userMatch = useMatch("/users/:id");
  const matchedUser = userMatch
    ? users.find((user) => user.id === userMatch.params.id)
    : null;

  useEffect(() => {
    dispatch(loadUser());
  }, []);

  useEffect(() => {
    dispatch(initializeBlogs());
  }, []);

  useEffect(() => {
    dispatch(loadUsers());
  }, []);

  const notifyWith = (message, type = "info") => {
    dispatch(setNotification(message, type, 500));
  };

  const login = async (username, password) => {
    dispatch(loginUser(username, password));
  };

  const logout = async () => {
    dispatch(logoutUser());
    notifyWith("logged out");
  };

  if (!user || user === "error") {
    return (
      <div>
        <h2>log in to application</h2>
        <Notification />
        <LoginForm login={login} />
      </div>
    );
  }

  const padding = {
    padding: 5,
  };

  return (
    <div>
      <h2>blogs</h2>
      <Notification />
      <div>
        <Link style={padding} to="/blogs">
          blogs
        </Link>
        <Link style={padding} to="/users">
          users
        </Link>
        {user.username} logged in
        <p>
          <button onClick={logout}>logout</button>
        </p>
      </div>
      <Routes>
        <Route
          path="/blogs"
          element={
            <Blogs user={user} notifyWith={notifyWith} dispatch={dispatch} />
          }
        />
        <Route path="/users" element={<Users />} />
        <Route path=":id" element={<User user={matchedUser} />} />
      </Routes>
    </div>
  );
};

export default App;
