import { createSlice } from "@reduxjs/toolkit";
import blogs from "../services/blogs";

const blogsSlice = createSlice({
  name: "blogs",
  initialState: [],
  reducers: {
    setBlogs(state, action) {
      return action.payload;
    },
    appendBlog(state, action) {
      state.push(action.payload);
    },
    updateBlog(state, action) {
      const targetId = action.payload.id;
      const targetBlog = state.find((blog) => blog.id === targetId);
      return state.map((b) => (b.id === targetId ? action.payload : b));
    },
    deleteBlog(state, action) {},
  },
});

export const { setBlogs, appendBlog } = blogsSlice.actions;

export const initializeBlogs = () => {
  return async (dispatch) => {
    const initialBlogs = await blogs.getAll();
    dispatch(setBlogs(initialBlogs));
  };
};

export const addBlog = (blog) => {
  return async (dispatch) => {
    const newBlog = await blogs.create(blog);
    dispatch(appendBlog(newBlog));
  };
};

export const upvoteBlog = (blog) => {
  return async (dispatch) => {
    const updatedBlog = await blogs.update(blog);
    dispatch(updateBlog(updatedBlog));
  };
};

export default blogsSlice.reducer;
