import { useEffect, useRef } from "react";
import Blog from "./components/Blog";
import { setNotification } from "./reducers/notificationReducer";
import {
  initializeBlogs,
  addBlog,
  changeBlog,
  removeBlog,
} from "./reducers/blogsReducer";
import { loginUser, logoutUser, loadUser } from "./reducers/userReducer";
import { useDispatch, useSelector } from "react-redux";

import LoginForm from "./components/Login";
import NewBlog from "./components/NewBlog";
import Notification from "./components/Notification";
import Togglable from "./components/Togglable";

const App = () => {
  const blogFormRef = useRef();

  const dispatch = useDispatch();

  const blogs = useSelector((state) => {
    console.log("Blogs: ", state.blogs);
    return [...state.blogs];
  });

  const user = useSelector((state) => {
    console.log("User: ", state.user);
    return state.user;
  });

  useEffect(() => {
    const user = dispatch(loadUser());
  }, []);

  useEffect(() => {
    dispatch(initializeBlogs());
  }, []);

  const notifyWith = (message, type = "info") => {
    console.log("notifying with: ", message, ", ", type);
    dispatch(setNotification(message, type, 500));
  };

  const login = async (username, password) => {
    dispatch(loginUser(username, password));
    console.log("Current User: ", user);
    if (user != "error") notifyWith("welcome!");
    else notifyWith("wrong username or password", "error");
  };

  const logout = async () => {
    dispatch(logoutUser());
    notifyWith("logged out");
  };

  const createBlog = async (newBlog) => {
    dispatch(addBlog(newBlog));
    notifyWith(`A new blog '${newBlog.title}' by '${newBlog.author}' added`);
    blogFormRef.current.toggleVisibility();
  };

  const like = async (blog) => {
    const blogToUpdate = { ...blog, likes: blog.likes + 1, user: blog.user.id };
    dispatch(changeBlog(blogToUpdate));
    notifyWith(`A like for the blog '${blog.title}' by '${blog.author}'`);
  };

  const remove = async (blog) => {
    const ok = window.confirm(
      `Sure you want to remove '${blog.title}' by ${blog.author}`
    );
    if (ok) {
      dispatch(removeBlog(blog));
      notifyWith(`The blog' ${blog.title}' by '${blog.author} removed`);
    }
  };

  if (!user) {
    return (
      <div>
        <h2>log in to application</h2>
        <Notification />
        <LoginForm login={login} />
      </div>
    );
  }

  const byLikes = (b1, b2) => b2.likes - b1.likes;

  return (
    <div>
      <h2>blogs</h2>
      <Notification />
      <div>
        {user.username} logged in
        <button onClick={logout}>logout</button>
      </div>
      <Togglable buttonLabel="new note" ref={blogFormRef}>
        <NewBlog createBlog={createBlog} />
      </Togglable>
      <div>
        {blogs.sort(byLikes).map((blog) => (
          <Blog
            key={blog.id}
            blog={blog}
            like={() => like(blog)}
            canRemove={user && blog.user.username === user.username}
            remove={() => remove(blog)}
          />
        ))}
      </div>
    </div>
  );
};

export default App;
